import requests


class myThread(threading.Thread):
    def __init__(self, chat_, text_tk):
        threading.Thread.__init__(self)
        self.chat = chat_
        self.text = text_tk
        api_key = "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"
        self.url = 'https://api.openai.com/v1/chat/completions'
        self.heards = {
            'Authorization': f'Bearer {api_key}',
            "Content-Type": "application/json"
        }

    def run(self):

        data = {

            "model": "gpt-3.5-turbo",
            "temperature": 0.7,
            "messages":
                [
                    {"role": "system", "content": "将下面日文翻译为中文"},
                    {"role": "user", "content": self.chat},
                ]

        }
        res = requests.post(self.url, headers=self.heards, data=json.dumps(data))
        r = res.json()['choices'][0]['message']['content'].split('\n')
     #   threadLock.acquire()
        with open("D:\\1.txt", "a") as f:
            for i in range(len(self.text)):
                f.write(f'"{self.text[i]}":"{r[i]}",\n')
                print(f'"{self.text[i]}" 翻译为 "{r[i]}"')
     #   threadLock.release()
        print('完成', len(self.chat))



